import { Link } from 'react-router-dom';
import { useApi } from '../api';
import { useEffect, useState } from 'react';
import './questions.css'
/*==========================================================
 * Home component
 */

export default function Home() {
  const api = useApi();
  const [summary, setSummary] = useState('');
  const [content, setContent] = useState('');
const [counter, setCounter] = useState(0);
let username = api.username;
  const [questions, setQuestions] = useState(undefined);
  useEffect(()=>{
    api.getQuestions().then(setQuestions); 
  },[counter]);
  let validationErrorText = '';
  async function submitHandler(event){
    event.preventDefault();
    if(summary.length >=10 && summary.length <= 100){
      if(content.length >= 1 && content.length <= 1000){
        await api.postQuestion(username, summary, content);
        setSummary('');
        setContent('');
        
      }else{
        validationErrorText += 'Make sure that your content is between 1 and 1000 characters'
      }
    }else{
      validationErrorText += '\nMake sure that your summary is between 10 and 100 characters'
    }
    


  }

  if(questions == undefined){
    return <div>Loading questions...</div>
  }else{
    let questionsContainer = [];
   
      for(let i=0; i<questions.length;i++){
        let likedUser = questions[i].likedByUser;
        let likedContent;

        if(likedUser){
          likedContent = <p>&hearts;</p>
        }else{
          likedContent = <p>â™¡</p>
        }
        async function toggleLike(event){
          if(likedUser){
          await api.deleteLike(questions[i].id);
          }else{
            await api.postLike(questions[i].id)
          }
          setCounter(counter+1);
        }
        questionsContainer.push( 
        <div className="questionContainer">
          {likedContent}
          <button onClick={toggleLike}>{likedUser? 'Dislike' : 'Like'}</button>
          <p>{questions[i].author}</p>
          <p>{questions[i].summary}</p>
          <p>{questions[i].likeCount}</p>
          <Link to={'/questions/'+questions[i].id}>View full question</Link>
        </div>);
      }

  return (
    <>
      <h1>Ask a Stupid Question!</h1>
      <form onSubmit={submitHandler}>
        <input type="text" value={summary} onChange={(e)=>setSummary(e.target.value)} placeholder='         Enter a summary'>
        </input>
        <br></br>
        <input type="text" value={content} onChange={(e)=>setContent(e.target.value)} placeholder='       Enter your question'>
        </input>
<p className="error">{validationErrorText}</p>
        <br></br><button type="submit">  Send
        </button>

      </form>
      {questionsContainer}
      <p>
        <Link to="/logout">Log out</Link>
      </p>
    </>
  )
}
}
