import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useApi } from "../api";
import './questions.css'
export default function QuestionDetails(){
    let params = useParams();
    const api= useApi();
    let questionid = params.questionid;
    const [content, setContent] = useState('');
    const [counter, setCounter] = useState(0);
    const [question, setQuestion] = useState(undefined);
    const [answers, setAnswers] = useState(undefined)
    let username = api.username;
    useEffect(()=>{
        api.getOneQuestion(questionid).then(setQuestion);
        api.getAnswers(questionid).then(setAnswers);
    },[counter]);

    async function submitHandler(event){
        event.preventDefault();
        if(content.length >= 1 && content.length <= 1000){
        await api.postAnswer(username, content, questionid);
        setContent('');
        setCounter(counter+1);
        }else{
          setValidationError('Your content should be between 1 and 1000 characters');
        }
      }
  
    if(question == undefined){
        return <div>Loading question...</div>
      }else{
        let likedUser = question.likedByUser;
        let likedContent;
  
        if(likedUser){
          likedContent = <p>&hearts;</p>
        }else{
          likedContent = <p>â™¡</p>
        }
        async function toggleLike(event){
          if(likedUser){
          await api.deleteLike(question.id);
          }else{
            await api.postLike(question.id);
          }
          setCounter(counter+1);
        }
        let answersObj =[];
        if(answers != undefined){
            
        
        for(let i = 0; i < answers.length; i++){
            let likedUserA = answers[i].likedByUser;
            let likedContentA;
      
            async function toggleLikeA(event){
                if(likedUserA){
                await api.deleteLike(answers[i].id);
                }else{
                  await api.postLike(answers[i].id);
                }
                setCounter(counter+1);
              }
     
              if(likedUserA){
                likedContentA = <p>&hearts;</p>
              }else{
                likedContentA = <p>â™¡</p>
              }
            answersObj.push(<div className="answerContainer">
                {likedContentA}
                <button onClick={toggleLikeA}>{likedUserA? 'Dislike' : 'Like'}</button><p>{answers[i].likeCount}</p>
                <p>{answers[i].author} responded:</p>
                <p>{answers[i].contents}</p>
            </div>)
        }
    }
    return(
    <>
        <div className="questionContainer">
          {likedContent}
          <button onClick={toggleLike}>{likedUser? 'Dislike' : 'Like'}</button>
         
        <p>{question.author}</p>
        <p>{question.summary}</p>
        <p>{question.contents}</p>
        <p>{question.likeCount}</p>

        <form onSubmit={submitHandler}>
        <input type="text" value={content} onChange={(e)=>setContent(e.target.value)}>
        </input>
        <button type="submit">
          Send
        </button>
        <p className="error">{validationError}</p>
      </form>
    </div>
    
{answersObj}
</>)
      }

}
