import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useApi } from "../api";

export default function QuestionDetails(){
    let params = useParams();
    const api= useApi();
    let questionid = params.questionid;
    const [content, setContent] = useState('');
    const [counter, setCounter] = useState(0);
    const [question, setQuestion] = useState(undefined);
    let username = api.username;
    useEffect(()=>{
        api.getOneQuestion(questionid).then(setQuestion);
    },[counter]);

    async function submitHandler(event){
        event.preventDefault();
        await api.postAnswer(username, content, questionid);
        setContent('');
        setCounter(counter+1);
      }
  
    if(question == undefined){
        return <div>Loading question...</div>
      }else{
        let likedUser = question.likedByUser;
        let likedContent;
  
        if(likedUser){
          likedContent = <p>user likes it</p>
        }else{
          likedContent = <p>user does not like it</p>
        }
        async function toggleLike(event){
          if(likedUser){
          await api.deleteLike(question.id);
          }else{
            await api.postLike(question.id)
          }
          setCounter(counter+1);
        }
    return(<div>
          {likedContent}
          <button onClick={toggleLike}>{likedUser? 'Dislike' : 'Like'}</button>
         
        <p>{question.author}</p>
        <p>{question.summary}</p>
        <p>{question.contents}</p>
        <form onSubmit={submitHandler}>
        <input type="text" value={content} onChange={(e)=>setContent(e.target.value)}>
        </input>

        <button type="submit">
          Send
        </button>

      </form>
    </div>)
      }

}
