import { Link } from 'react-router-dom';
import { useApi } from '../api';
import { useEffect, useState } from 'react';

/*==========================================================
 * Home component
 */

export default function Home() {
  const api = useApi();
  const [summary, setSummary] = useState('');
  const [content, setContent] = useState('');
const [counter, setCounter] = useState(0);
let username = api.username;
  const [questions, setQuestions] = useState(undefined);
  useEffect(()=>{
    api.getQuestions().then(setQuestions); 
  },[counter]);

  async function submitHandler(event){
    event.preventDefault();
    await api.postQuestion(username, summary, content);
    setSummary('');
    setContent('');
    setCounter(counter+1);
  }

  if(questions == undefined){
    return <div>Loading questions...</div>
  }else{
    let questionsContainer = [];
   
      for(let i=0; i<questions.length;i++){
        let likedUser = questions[i].likedByUser;
        let likedContent;
        if(likedByUser){
          likedContent = <p>user likes it</p>
        }else{
          likedContent = <p>user does not like it</p>
        }
        async function toggleLike(event){
          if(likedUser){
          await api.deleteLike(questions[i].id);
          }else{
            await api.create(questions[i].id)
          }
          setCounter(counter+1);
        }
        questionsContainer.push( 
        <div>
          {likedContent}
          <button onClick={toggleLike}>{likedUser? 'Dislike' : 'Like'}</button>
          <p>{questions[i].author}</p>
          <p>{questions[i].summary}</p>
          <p>{questions[i].likeCount}</p>
          <Link to={'/questions/'+questions[i].id}>View full question</Link>
        </div>);
      }
    
  return (
    <>
      <h1>Ask a Stupid Question!</h1>
      <form onSubmit={submitHandler}>
        <input type="text" value={summary} onChange={(e)=>setSummary(e.target.value)}>
        </input>

        <input type="text" value={content} onChange={(e)=>setContent(e.target.value)}>
        </input>

        <button type="submit">
          Send
        </button>

      </form>
      {questionsContainer}
      <p>
        <Link to="/logout">Log out</Link>
      </p>
    </>
  )
}
}
