import { Link } from 'react-router-dom';
import { useApi } from '../api';
import { useEffect, useState } from 'react';

/*==========================================================
 * Home component
 */

export default function Home() {
  const api = useApi();
  const [summary, setSummary] = useState('');
  const [content, setContent] = useState('');
const [counter, setCounter] = useState(0);
  const [questions, setQuestions] = useState(undefined);
  useEffect(()=>{
    api.getQuestions().then(setQuestions); 
  },[counter]);

  async function submitHandler(event){
    event.preventDefault();
    await api.postQuestion('dlainez1', summary, content);
    setSummary('');
    setContent('');
    setCounter(counter+1);
  }

  if(questions == undefined){
    return <div>Loading questions...</div>
  }else{
    let questionsContainer = [];
   
      for(let i=0; i<questions.length;i++){
        questionsContainer.push( 
        <div>
          <p>{questions[i].author}</p>
          <p>{questions[i].summary}</p>
          <p>{questions[i].likeCount}</p>
          <Link to={'/questions/'+questions[i].id}>View full question</Link>
        </div>);
      }
    
  return (
    <>
      <h1>Ask a Stupid Question!</h1>
      <form onSubmit={submitHandler}>
        <input type="text" value={summary} onChange={(e)=>setSummary(e.target.value)}>
        </input>

        <input type="text" value={content} onChange={(e)=>setContent(e.target.value)}>
        </input>

        <button type="submit">
          Send
        </button>

      </form>
      {questionsContainer}
      <p>
        <Link to="/logout">Log out</Link>
      </p>
    </>
  )
}
}
